import { useState } from 'react/cjs/react.development';

const Test2 = () => {

    const [cnt,setCnt] = useState(1)
    const ref1 = useRef()
    const ref2 = useRef()

    const onColor = ()=>{
        SVGTextContentElement(cnt +1)
        ref1.current.stlye.color = 'pink'
        ref2.current.stlye.color = 'green'
    }

    return (
        <div>
            <h2> Hooks = useEffect 설명</h2>
            <h1>카운트 : {cnt} </h1>
            <h1 ref={ref1}>useEffect</h1>
            <h1 ref={ref2}>useLayoutEffect</h1>
            <button onClick={onColor}>컬러와 숫자 증가</button>


        </div>
    );
};

export default Test2;
