import React, { useState } from 'react';
import FriendList from './FriendList';
import './style.css'
import FriendInput from './FriendInput';
import dataList from '../assets/api/frienddata'

const Friend = () => {
    const [data,setData] = useState(dataList)
    const [isView, setIsView] = useState(false)

    const onDel = (id) => {
        setData(data.filter(item =>item.id !== id))
    }

    const onRemove = () => {
        setData([])
    }

    const onRestore = () => {
        setData(dataList)
    }
    const onToggle = (id) => {
        setData(data.map(item => item.id === id ? {...item , isChecked:!item.isChecked} : item))
    }
    return (
        <div className='wrap'>
            <h1> 친구들 총인원 : {data.length}명</h1>
            <p className = 'chk'>
            <input type="checkbox" checked={isView} onChange={e => setIsView(e.target.checked)}/>
                추가 비활성 / 추가활성
            </p>
            <FriendList data={data} onDel={onDel}/>
            <p className='btn'>
                <button onClick={onRemove}>모두삭제</button>
                <button onClick={onRestore}>초기 복구</button>
            </p>
            {
                isView && <FriendInput />
            }

        </div>
    );
};

export default Friend;
